package com.e.loadpicfromurl;

import androidx.appcompat.app.AppCompatActivity;

import android.os.Bundle;
import android.view.View;
import android.widget.Button;
import android.widget.ImageView;

import com.bumptech.glide.Glide;

public class MainActivity extends AppCompatActivity {
 ImageView lfu;
 Button first,second,third;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        first=findViewById(R.id.b1);
        second=findViewById(R.id.b2);
        third=findViewById(R.id.b3);
        lfu=findViewById(R.id.lfu);
        first.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
           Glide.with(MainActivity.this).load("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ52nRiLpVqCdyyfHjH3Z5gRnEWwdZTWneLog&usqp=CAU").into(lfu);
            }
        });
        second.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                Glide.with(MainActivity.this).load("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRSgFQlpbqKL7HRiGWW4zRciOQ8FmFRMwWfSQ&usqp=CAU").into(lfu);

            }
        });
        third.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                Glide.with(MainActivity.this).load("data:image/jpeg;base64,").into(lfu);

            }
        });


    }
}